
public class Loop {

	public static void main(String[] args) {
//		*****
//		****
//		**
//		*
		// TODO Auto-generated method stub
		String name = "JAYANT";
		for ( int i= 0; i <=5; i++) {
			for (int j = i; j <= 5; j++) {
				System.out.print(name.toCharArray()[j]+ " ");
			}
			System.out.println();

		}

	}

}
